package loops;

public class WhileLoop 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int i=1;  //Initialization
		
		while(i<10)  //Condition
		{
			System.out.println(i);
			i++; 			//Increment
		}
		
		int k=10;  //Initialization
		
		while(k>0)  //Condition
		{
			System.out.println(k);
			k--; 			//Decrement
		}		
	}

}
