package variableAndDatatypes;

public class ArithmaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=50;
		int b=20;
		float c=(a/b);
		
		System.out.println(a+b); //
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(c);
		System.out.println(50%20);
		
				
	}

}
