package variableAndDatatypes;

public class Class2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Numbers
		int age=30; // variable "age" is declared with datatype "int" and initialised with value "30"
		System.out.println(age);
		
		//Characters
		char gender='M'; // variable "gender" is declared with datatype "char" and initialised with value "M"
	
		//true or false
		boolean flag=true; // variable "flag" is declared with datatype "boolean" and initialised with value "true"
		
		//decimal values
		double amt=789.57;
		
		//string values
		String name="Swati";
		
		//Less used datatypes
		//Numbers
		byte a=127;
		short b=20;
		long l=667897979;
		
		//Decimal
		float f=67.67f;
	}

}
